sys_variable_value.value:
(function execute(inputs, outputs) {
  var region = inputs.region.trim();
  var session = gs.getSession();
  session.putClientData('SN_AWS_OPSWORK_REGION', region || "us-east-1");
  outputs.region = region;
  if (inputs.stack_id != "") {
    var payload = {
      "StackId": inputs.stack_id
    }
    outputs.payload = JSON.stringify(payload)
  } else {
    outputs.payload = JSON.stringify({})
  }
})(inputs, outputs);

