sys_variable_value.value:
(function execute(inputs, outputs) {
  var temp = JSON.parse(inputs.response_body);
  if (inputs.status == 200) {
    outputs.status = "Success";
      outputs.arn = temp.StackSummary.Arn;
      outputs.online_instances = temp.StackSummary.InstancesCount.Online;
      outputs.stopped_instances = temp.StackSummary.InstancesCount.Stopped;
      outputs.layers =  temp.StackSummary.LayersCount;
      outputs.name =  temp.StackSummary.Name;  
      outputs.appscount =  temp.StackSummary.AppsCount;
  } else {
    outputs.status = "Error";
    outputs.error_message = temp.message;
  }
})(inputs, outputs);

