sys_variable_value.value:
(function execute(inputs, outputs) {
  var temp = JSON.parse(inputs.response_body);
  if (inputs.status == 200) {
    outputs.status = "Success";
    outputs.Permissions = temp.Permissions
  } else {
    outputs.status = "Error";
    outputs.error_message = temp.message;
  }
})(inputs, outputs);

