sys_variable_value.value:
(function execute(inputs, outputs) {
   if (inputs.iam_use_arn.trim() === "" && inputs.stack_id.trim() === "")
  throw new Error("Either of IAM User ARN or Stack ID is Mandatory");
  var region = inputs.region.trim();
  var session = gs.getSession();
  session.putClientData('SN_AWS_OPSWORK_REGION', region || "us-east-1");
  outputs.region = region;
    var payload = {
      "StackId": inputs.stack_id.trim(),
      "IamUserArn" : inputs.iam_use_arn.trim()
    }
    outputs.payload = JSON.stringify(payload)
})(inputs, outputs);

