sys_variable_value.value:
(function execute(inputs, outputs) {
  var temp = {}
  for (var i = 0; i < inputs.tags.length; i++) {
    temp[inputs.tags[i].Key.trim()] = inputs.tags[i].Value.trim()
  }
  var region = inputs.region.trim();
  var session = gs.getSession();
  session.putClientData('SN_AWS_OPSWORK_REGION', region || "us-east-1");
  outputs.region = region;
  var payload = {
    "ResourceArn": inputs.resource_arn.trim(),
    "Tags": temp,
  }
  outputs.payload = JSON.stringify(payload)
})(inputs, outputs);

